 * - Layout ÁªÑ‰ª∂Êèê‰æõÁªü‰∏ÄÁöÑÈ°µÈù¢Â∏ÉÂ±ÄÂíåÂØºËà™
 * - ÂêÑ‰∏™È°µÈù¢ÁªÑ‰ª∂‰∏ìÊ≥®‰∫éËá™Â∑±ÁöÑÂäüËÉΩÂÆûÁé∞
 * - ‰øùÊåÅËÆæËÆ°‰∏ÄËá¥ÊÄßÂíå‰ª£Á†ÅÊ®°ÂùóÂåñ
 */
function App() {
  // -----------------------
  // ÂÖ®Â±ÄÁä∂ÊÄÅ (Global State)
  // -----------------------
  const [messages, setMessages] = React.useState<Array<{id: number, role: 'user' | 'assistant', content: string, timestamp: string, model_used?: string}>>([]); 
  // ËÅäÂ§©ËÆ∞ÂΩïÔºöÁî®Êà∑ÂíåÂä©ÊâãÁöÑÊâÄÊúâÊ∂àÊÅØ

  const [isLoading, setIsLoading] = React.useState(false); // ÊòØÂê¶Ê≠£Âú®Á≠âÂæÖAIÂõûÂ§ç (ÊéßÂà∂ËæìÂÖ•Ê°ÜÁ¶ÅÁî®„ÄÅÊòæÁ§∫TypingIndicator)
  const [models, setModels] = React.useState<ModelInfo[]>([]); // ÂèØÁî®ÁöÑÊ®°ÂûãÂàóË°® (‰∏ãÊãâÊ°Ü)
  const [apiStatus, setApiStatus] = React.useState<ApiStatus | null>(null); // APIÂÅ•Â∫∑Áä∂ÊÄÅ (ÂÅ•Â∫∑/ÈôçÁ∫ß/ÈîôËØØ)
  const [error, setError] = React.useState<string | null>(null); // ÈîôËØØÊèêÁ§∫ (È°∂ÈÉ®Á∫¢Ëâ≤banner)
  const [isInitialized, setIsInitialized] = React.useState(false); // Â∫îÁî®ÊòØÂê¶ÂàùÂßãÂåñÂÆåÊØï (Âä†ËΩΩÊ®°Âûã+Áä∂ÊÄÅ)

  const messagesEndRef = React.useRef<HTMLDivElement>(null); // Áî®Êù•Ê†áËÆ∞Ê∂àÊÅØÂàóË°®Â∫ïÈÉ®ÁöÑDOMËäÇÁÇπ (Áî®‰∫éËá™Âä®ÊªöÂä®)

  // -----------------------
  // ÊªöÂä®ÈÄªËæë (Side Effect)
  // -----------------------
  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  // ÊØèÂΩì messages Êàñ isLoading ÊîπÂèòÊó∂ÔºåËá™Âä®ÊªöÂä®Âà∞ÊúÄÂ∫ïÈÉ®
  React.useEffect(() => {
    scrollToBottom();
  }, [messages, isLoading]);

  // -----------------------
  // ÂàùÂßãÂåñÈÄªËæë (App init)
  // -----------------------
  React.useEffect(() => {
    const initializeApp = async () => {
      try {
        setError(null);

        // Âπ∂Ë°åËØ∑Ê±ÇÔºöÂä†ËΩΩÊ®°ÂûãÂàóË°® & APIÁä∂ÊÄÅ
        const [modelsResponse, statusResponse] = await Promise.all([
          ApiService.getModels(),
          ApiService.getStatus()
        ]);

        setModels(modelsResponse.models);
        setApiStatus(statusResponse);
        setIsInitialized(true); // ÂàùÂßãÂåñÂÆåÊàê
      } catch (error: any) {
        console.error('Failed to initialize app:', error);
        setError(error.message || 'Failed to connect to the API');
        setIsInitialized(true); // Âç≥‰ΩøÂ§±Ë¥•‰πüÊ†áËÆ∞ÂàùÂßãÂåñÂÆåÊàêÔºåÈÅøÂÖç‰∏ÄÁõ¥loading
      }
    };

    initializeApp();
  }, []);

  // -----------------------
  // ÂèëÈÄÅÊ∂àÊÅØÈÄªËæë
  // -----------------------
  const handleSendMessage = async (request: ChatRequest) => {
    if (isLoading) return; // Èò≤Ê≠¢ÈáçÂ§çÁÇπÂáª

    setIsLoading(true);
    setError(null);

    // 1) Á´ãÂç≥Ê∑ªÂä†Áî®Êà∑Ê∂àÊÅØ (optimistic UI)
    const userMessage = {
      id: Date.now(),
      role: 'user' as const,
      content: request.message,
      timestamp: new Date().toISOString()
    };
    setMessages(prev => [...prev, userMessage]);

    try {
      // 2) Ë∞ÉÁî®APIÔºåÁ≠âÂæÖAIÂõûÂ§ç
      const response = await ApiService.sendChatMessage(request);

      // 3) Ê∑ªÂä†Âä©ÊâãÁöÑÂõûÂ§ç
      const assistantMessage = {
        id: Date.now() + 1,
        role: 'assistant' as const,
        content: response.response,
        timestamp: response.timestamp,
        model_used: response.model_used
      };
      setMessages(prev => [...prev, assistantMessage]);

    } catch (error: any) {
      console.error('Failed to send message:', error);
      setError(error.message || 'Failed to send message');
    } finally {
      setIsLoading(false); // ËØ∑Ê±ÇÂÆåÊàê
    }
  };

  // Ê∏ÖÁ©∫ËÅäÂ§©ËÆ∞ÂΩï
  const clearChat = () => {
    setMessages([]);
    setError(null);
  };

  // Âà∑Êñ∞APIÁä∂ÊÄÅ
  const refreshStatus = async () => {
    try {
      const status = await ApiService.getStatus();
      setApiStatus(status);
    } catch (error: any) {
      console.error('Failed to refresh status:', error);
    }
  };

  // -----------------------
  // ÂàùÂßãÂåñÂä†ËΩΩÁïåÈù¢ (Loading screen)
  // -----------------------
  if (!isInitialized) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <div className="w-8 h-8 border-4 border-primary-500 border-t-transparent rounded-full animate-spin mx-auto mb-4"></div>
          <p className="text-gray-600">Initializing LLM Practice App...</p>
        </div>
      </div>
    );
  }

  // -----------------------
  // ‰∏ªÁïåÈù¢Ê∏≤Êüì
  // -----------------------
  return (
    <Router>
      <Layout>
        <Routes>
          {/* ËÅäÂ§©È°µÈù¢ - ÈªòËÆ§È¶ñÈ°µ */}
          <Route path="/" element={
            <div className="min-h-screen bg-gray-50 flex flex-col">
              {/* È°∂ÈÉ® Header */}
              <header className="bg-white shadow-sm border-b border-gray-200">
                <div className="max-w-4xl mx-auto px-4 py-4">
                  <div className="flex items-center justify-between">
                    <div>
                      <h1 className="text-2xl font-bold text-gray-900">LLM Practice</h1>
                      <p className="text-sm text-gray-600">
                        Practice chatting with Large Language Models
                      </p>
                    </div>

                    <div className="flex items-center space-x-4">
                      {/* API Áä∂ÊÄÅÊåáÁ§∫Âô® */}
                      {apiStatus && (
                        <div className="flex items-center space-x-2">
                          <div className={`w-3 h-3 rounded-full ${
                            apiStatus.overall === 'healthy' ? 'bg-green-500' : 
                            apiStatus.overall === 'degraded' ? 'bg-yellow-500' : 'bg-red-500'
                          }`}></div>
                          <span className="text-sm text-gray-600">
                            {apiStatus.overall === 'healthy' ? 'Connected' : 'Issues detected'}
                          </span>
                          <button
                            onClick={refreshStatus}
                            className="text-xs text-primary-600 hover:text-primary-700"
                          >
                            Refresh
                          </button>
                        </div>
                      )}

                      {/* Ê∏ÖÁ©∫ËÅäÂ§©ÊåâÈíÆ */}
                      <button
                        onClick={clearChat}
                        className="px-3 py-1 text-sm bg-gray-100 text-gray-700 rounded-md hover:bg-gray-200 transition-colors"
                      >
                        Clear Chat
                      </button>
                    </div>
                  </div>
                </div>
              </header>

              {/* ÈîôËØØÊèêÁ§∫ Banner */}
              {error && (
                <div className="bg-red-50 border-l-4 border-red-400 p-4">
                  <div className="max-w-4xl mx-auto">
                    <div className="flex items-center">
                      <div className="flex-shrink-0">
                        {/* ÈîôËØØÂõæÊ†á */}
                        <svg className="h-5 w-5 text-red-400" viewBox="0 0 20 20" fill="currentColor">
                          <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zM8.707 7.293a1 1 0 00-1.414 1.414L8.586 10l-1.293 1.293a1 1 0 101.414 1.414L10 11.414l1.293 1.293a1 1 0 001.414-1.414L11.414 10l1.293-1.293a1 1 0 00-1.414-1.414L10 8.586 8.707 7.293z" clipRule="evenodd" />
                        </svg>
                      </div>
                      <div className="ml-3">
                        <p className="text-sm text-red-700">{error}</p>
                      </div>
                      <div className="ml-auto">
                        <button
                          onClick={() => setError(null)}
                          className="text-red-400 hover:text-red-600"
                        >
                          <svg className="h-5 w-5" viewBox="0 0 20 20" fill="currentColor">
                            <path fillRule="evenodd" d="M4.293 4.293a1 1 0 011.414 0L10 8.586l4.293-4.293a1 1 0 111.414 1.414L11.414 10l4.293 4.293a1 1 0 01-1.414 1.414L10 11.414l-4.293 4.293a1 1 0 01-1.414-1.414L8.586 10 4.293 5.707a1 1 0 010-1.414z" clipRule="evenodd" />
                          </svg>
                        </button>
                      </div>
                    </div>
                  </div>
                </div>
              )}

              {/* ‰∏ª‰ΩìËÅäÂ§©Âå∫ */}
              <main className="flex-1 overflow-hidden">
                <div className="max-w-4xl mx-auto h-full flex flex-col">
                  {/* ËÅäÂ§©Ê∂àÊÅØÂå∫ (Â∏¶ÊªöÂä®Êù°) */}
                  <div className="flex-1 overflow-y-auto scrollbar-thin p-4">
                    {messages.length === 0 && !isLoading ? (
                      // Á©∫Áä∂ÊÄÅÊèêÁ§∫
                      <div className="text-center py-12">
                        <div className="text-6xl mb-4">ü§ñ</div>
                        <h2 className="text-xl font-semibold text-gray-700 mb-2">
                          Welcome to LLM Practice!
                        </h2>
                        <p className="text-gray-600 max-w-md mx-auto">
                          Start a conversation with an AI assistant. Ask questions, get help with coding, 
                          or just have a chat. Your conversation history will be saved automatically.
                        </p>
                      </div>
                    ) : (
                      // Ê∏≤ÊüìÊ∂àÊÅØÂàóË°®
                      <div className="space-y-4">
                        {messages.map((message, index) => (
                          <ChatMessage
                            key={message.id}
                            message={message}
                            isLatest={index === messages.length - 1}
                          />
                        ))}
                        {isLoading && <TypingIndicator />} 
                      </div>
                    )}
                    {/* Â∫ïÈÉ®ÈîöÁÇπÔºåÁî®‰∫éËá™Âä®ÊªöÂä® */}
                    <div ref={messagesEndRef} />
                  </div>

                  {/* ËæìÂÖ•Ê°ÜÁªÑ‰ª∂ */}
                  <ChatInput
                    onSendMessage={handleSendMessage}
                    isLoading={isLoading}
                    models={models}
                  />
                </div>
              </main>
            </div>
          } />

          {/* ÂÖ≥‰∫éÈ°µÈù¢ */}
          <Route path="/about" element={<AboutPage />} />

          {/* 404 È°µÈù¢ - ÈáçÂÆöÂêëÂà∞È¶ñÈ°µ */}
          <Route path="*" element={<ChatPage />} />
        </Routes>
      </Layout>
    </Router>
  );
}

export default App;
