import React from 'react';
import { ConversationHistory } from '../types/api';

interface ChatMessageProps {
  message: ConversationHistory;
  isLatest?: boolean;
}

const ChatMessage: React.FC<ChatMessageProps> = ({ message, isLatest = false }) => {
  const formatTimestamp = (timestamp: string) => {
    const date = new Date(timestamp);
    return date.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
  };

  return (
    <div className={`message-fade-in ${isLatest ? 'mb-4' : 'mb-6'}`}>
      {/* User Message */}
      <div className="flex justify-end mb-3">
        <div className="max-w-xs lg:max-w-md">
          <div className="bg-primary-500 text-white rounded-lg px-4 py-2 shadow-sm">
            <p className="text-sm leading-relaxed">{message.user_message}</p>
          </div>
          <div className="flex justify-end mt-1">
            <span className="text-xs text-gray-500">
              {formatTimestamp(message.timestamp)}
            </span>
          </div>
        </div>
      </div>

      {/* Assistant Message */}
      <div className="flex justify-start">
        <div className="max-w-xs lg:max-w-md">
          <div className="bg-gray-100 text-gray-800 rounded-lg px-4 py-2 shadow-sm">
            <p className="text-sm leading-relaxed whitespace-pre-wrap">
              {message.assistant_response}
            </p>
          </div>
          <div className="flex justify-between items-center mt-1">
            <span className="text-xs text-gray-500">
              {message.model_used}
            </span>
            <span className="text-xs text-gray-500">
              {formatTimestamp(message.timestamp)}
            </span>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ChatMessage;
