import React, { useState, useEffect, useRef } from 'react';
import { ApiService } from '../services/api';
import { ChatRequest, ModelInfo } from '../types/api';
import ChatMessage from '../components/ChatMessage';
import ChatInput from '../components/ChatInput';
import TypingIndicator from '../components/TypingIndicator';

/**
 * ChatPage ÁªÑ‰ª∂ - ËÅäÂ§©È°µÈù¢
 * 
 * ÂäüËÉΩÔºö
 * - ÁÆ°ÁêÜËÅäÂ§©Ê∂àÊÅØÁä∂ÊÄÅ
 * - Â§ÑÁêÜÁî®Êà∑ÂèëÈÄÅÊ∂àÊÅØ
 * - ÊòæÁ§∫ AI ÂõûÂ§ç
 * - Ëá™Âä®ÊªöÂä®Âà∞ÊúÄÊñ∞Ê∂àÊÅØ
 * - Ê∏ÖÁ©∫ËÅäÂ§©ËÆ∞ÂΩï
 * 
 * Ëøô‰∏™È°µÈù¢‰∏ìÊ≥®‰∫éËÅäÂ§©ÂäüËÉΩÔºåÂ∏ÉÂ±ÄÁî± Layout ÁªÑ‰ª∂Êèê‰æõ
 */
const ChatPage: React.FC = () => {
  // -----------------------
  // ËÅäÂ§©Áä∂ÊÄÅ (Chat State)
  // -----------------------
  const [messages, setMessages] = useState<Array<{
    id: number, 
    role: 'user' | 'assistant', 
    content: string, 
    timestamp: string, 
    model_used?: string
  }>>([]); // ËÅäÂ§©ËÆ∞ÂΩïÔºöÁî®Êà∑ÂíåÂä©ÊâãÁöÑÊâÄÊúâÊ∂àÊÅØ

  const [isLoading, setIsLoading] = useState(false); // ÊòØÂê¶Ê≠£Âú®Á≠âÂæÖAIÂõûÂ§ç
  const [models, setModels] = useState<ModelInfo[]>([]); // ÂèØÁî®ÁöÑÊ®°ÂûãÂàóË°®
  const [error, setError] = useState<string | null>(null); // È°µÈù¢Á∫ßÈîôËØØÊèêÁ§∫

  const messagesEndRef = useRef<HTMLDivElement>(null); // Áî®Êù•Ê†áËÆ∞Ê∂àÊÅØÂàóË°®Â∫ïÈÉ®ÁöÑDOMËäÇÁÇπ

  // -----------------------
  // ÊªöÂä®ÈÄªËæë (Side Effect)
  // -----------------------
  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  // ÊØèÂΩì messages Êàñ isLoading ÊîπÂèòÊó∂ÔºåËá™Âä®ÊªöÂä®Âà∞ÊúÄÂ∫ïÈÉ®
  useEffect(() => {
    scrollToBottom();
  }, [messages, isLoading]);

  // -----------------------
  // ÂàùÂßãÂåñÈÄªËæë (Page init)
  // -----------------------
  useEffect(() => {
    const initializePage = async () => {
      try {
        setError(null);

        // Âä†ËΩΩÊ®°ÂûãÂàóË°®
        const modelsResponse = await ApiService.getModels();
        setModels(modelsResponse.models);
      } catch (error: any) {
        console.error('Failed to initialize chat page:', error);
        setError('Failed to load models. Please refresh the page.');
      }
    };

    initializePage();
  }, []);

  // -----------------------
  // Ê∂àÊÅØÂ§ÑÁêÜÈÄªËæë (Message handling)
  // -----------------------
  const handleSendMessage = async (request: ChatRequest) => {
    // Ê∑ªÂä†Áî®Êà∑Ê∂àÊÅØÂà∞ËÅäÂ§©ËÆ∞ÂΩï
    const userMessage = {
      id: Date.now(),
      role: 'user' as const,
      content: request.message,
      timestamp: new Date().toISOString()
    };

    setMessages(prev => [...prev, userMessage]);
    setIsLoading(true);
    setError(null);

    try {
      if (request.stream) {
        // ÊµÅÂºèÂìçÂ∫îÊ®°Âºè
        await handleStreamingResponse(request);
      } else {
        // Ê†áÂáÜÂìçÂ∫îÊ®°Âºè
        await handleStandardResponse(request);
      }
    } catch (error: any) {
      console.error('Chat error:', error);
      setError(error.message || 'Failed to get response from AI. Please try again.');
    } finally {
      setIsLoading(false);
    }
  };

  // Â§ÑÁêÜÊ†áÂáÜÂìçÂ∫î
  const handleStandardResponse = async (request: ChatRequest) => {
    const response = await ApiService.sendChatMessage(request);

    // Ê∑ªÂä† AI ÂõûÂ§çÂà∞ËÅäÂ§©ËÆ∞ÂΩï
    const assistantMessage = {
      id: Date.now() + 1,
      role: 'assistant' as const,
      content: response.response,
      timestamp: new Date().toISOString(),
      model_used: response.model_used
    };

    setMessages(prev => [...prev, assistantMessage]);
  };

  // Â§ÑÁêÜÊµÅÂºèÂìçÂ∫î
  const handleStreamingResponse = async (request: ChatRequest) => {
    let assistantMessageId = Date.now() + 1;
    let streamingContent = '';

    // ÂàõÂª∫ÂàùÂßãÁöÑÂä©ÊâãÊ∂àÊÅØÔºàÁ©∫ÂÜÖÂÆπÔºâ
    const initialAssistantMessage = {
      id: assistantMessageId,
      role: 'assistant' as const,
      content: '',
      timestamp: new Date().toISOString(),
      model_used: request.model || 'gpt-3.5-turbo'
    };

    setMessages(prev => [...prev, initialAssistantMessage]);

    await ApiService.sendChatMessageStream(request, {
      onStart: (data) => {
        console.log('Stream started:', data);
      },
      onContent: (data) => {
        if (data.content) {
          streamingContent += data.content;
          // ÂÆûÊó∂Êõ¥Êñ∞Ê∂àÊÅØÂÜÖÂÆπ
          setMessages(prev => 
            prev.map(msg => 
              msg.id === assistantMessageId 
                ? { ...msg, content: streamingContent }
                : msg
            )
          );
        }
      },
      onEnd: (data) => {
        console.log('Stream ended:', data);
        // Á°Æ‰øùÊúÄÁªàÂÜÖÂÆπÊòØÂÆåÊï¥ÁöÑ
        if (data.full_response) {
          setMessages(prev => 
            prev.map(msg => 
              msg.id === assistantMessageId 
                ? { 
                    ...msg, 
                    content: data.full_response || '',
                    model_used: data.model_used || msg.model_used
                  }
                : msg
            )
          );
        }
      },
      onError: (data) => {
        console.error('Stream error:', data);
        setError(data.error || 'Streaming failed');
        // ÁßªÈô§Â§±Ë¥•ÁöÑÊ∂àÊÅØ
        setMessages(prev => prev.filter(msg => msg.id !== assistantMessageId));
      }
    });
  };

  // Ê∏ÖÁ©∫ËÅäÂ§©ËÆ∞ÂΩï
  const clearChat = () => {
    setMessages([]);
    setError(null);
  };

  // -----------------------
  // È°µÈù¢Ê∏≤Êüì
  // -----------------------
  return (
    <div className="h-full flex flex-col">
      {/* È°µÈù¢Á∫ßÈîôËØØÊèêÁ§∫ */}
      {error && (
        <div className="bg-red-50 border border-red-200 rounded-md p-3 m-4 mb-0">
          <div className="flex items-center">
            <div className="flex-shrink-0">
              <svg className="h-5 w-5 text-red-400" viewBox="0 0 20 20" fill="currentColor">
                <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zM8.707 7.293a1 1 0 00-1.414 1.414L8.586 10l-1.293 1.293a1 1 0 101.414 1.414L10 11.414l1.293 1.293a1 1 0 001.414-1.414L11.414 10l1.293-1.293a1 1 0 00-1.414-1.414L10 8.586 8.707 7.293z" clipRule="evenodd" />
              </svg>
            </div>
            <div className="ml-3">
              <p className="text-sm text-red-700">{error}</p>
            </div>
            <div className="ml-auto">
              <button
                onClick={() => setError(null)}
                className="text-red-400 hover:text-red-600"
              >
                <svg className="h-5 w-5" viewBox="0 0 20 20" fill="currentColor">
                  <path fillRule="evenodd" d="M4.293 4.293a1 1 0 011.414 0L10 8.586l4.293-4.293a1 1 0 111.414 1.414L11.414 10l4.293 4.293a1 1 0 01-1.414 1.414L10 11.414l-4.293 4.293a1 1 0 01-1.414-1.414L8.586 10 4.293 5.707a1 1 0 010-1.414z" clipRule="evenodd" />
                </svg>
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Â∑•ÂÖ∑Ê†è */}
      <div className="flex justify-between items-center p-4 pb-0">
        <h2 className="text-lg font-semibold text-gray-900">AI Chat</h2>
        <button
          onClick={clearChat}
          className="px-3 py-1 text-sm bg-gray-100 text-gray-700 rounded-md hover:bg-gray-200 transition-colors"
        >
          Clear Chat
        </button>
      </div>

      {/* ËÅäÂ§©Ê∂àÊÅØÂå∫ (Â∏¶ÊªöÂä®Êù°) */}
      <div className="flex-1 overflow-y-auto scrollbar-thin p-4">
        {messages.length === 0 && !isLoading ? (
