import React, { useState, useEffect } from 'react';
import { Link, useLocation } from 'react-router-dom';
import { ApiService } from '../services/api';
import { ApiStatus } from '../types/api';

interface LayoutProps {
  children: React.ReactNode;
}

/**
 * Layout ÁªÑ‰ª∂ - Êèê‰æõÁªü‰∏ÄÁöÑÈ°µÈù¢Â∏ÉÂ±ÄÊ°ÜÊû∂
 * 
 * ÂäüËÉΩÔºö
 * - È°∂ÈÉ®ÂØºËà™Ê†èÔºàHeaderÔºâ
 * - È°µÈù¢Ë∑ØÁî±ÂØºËà™
 * - API Áä∂ÊÄÅÊòæÁ§∫
 * - ÈîôËØØÊèêÁ§∫ Banner
 * - Áªü‰∏ÄÁöÑÈ°µÈù¢ÂÆπÂô®ÂíåÊ†∑Âºè
 * 
 * ÊâÄÊúâÈ°µÈù¢ÈÉΩ‰ºöË¢´Ëøô‰∏™ Layout ÂåÖË£πÔºåÁ°Æ‰øùËÆæËÆ°‰∏ÄËá¥ÊÄß
 */
const Layout: React.FC<LayoutProps> = ({ children }) => {
  // -----------------------
  // ÂÖ®Â±ÄÁä∂ÊÄÅ (Global State)
  // -----------------------
  const [apiStatus, setApiStatus] = useState<ApiStatus | null>(null);
  const [error, setError] = useState<string | null>(null);
  const [isInitialized, setIsInitialized] = useState(false);

  const location = useLocation(); // Ëé∑ÂèñÂΩìÂâçË∑ØÁî±‰ΩçÁΩÆ

  // -----------------------
  // ÂàùÂßãÂåñÈÄªËæë (App init)
  // -----------------------
  useEffect(() => {
    const initializeApp = async () => {
      try {
        setError(null);

        // Ëé∑Âèñ API Áä∂ÊÄÅ
        const statusResponse = await ApiService.getStatus();
        setApiStatus(statusResponse);
        setIsInitialized(true);
      } catch (error: any) {
        console.error('Failed to initialize app:', error);
        setError('Failed to connect to the backend. Please check if the server is running.');
        setIsInitialized(true); // Âç≥‰ΩøÂá∫Èîô‰πüË¶ÅÊòæÁ§∫ÁïåÈù¢
      }
    };

    initializeApp();
  }, []);

  // Âà∑Êñ∞APIÁä∂ÊÄÅ
  const refreshStatus = async () => {
    try {
      const status = await ApiService.getStatus();
      setApiStatus(status);
    } catch (error: any) {
      console.error('Failed to refresh status:', error);
    }
  };

  // ÂØºËà™ËèúÂçïÈ°π
  const navigationItems = [
    { path: '/', label: 'Chat', icon: 'üí¨' },
    { path: '/about', label: 'About', icon: '‚ÑπÔ∏è' },
  ];

  // -----------------------
  // ÂàùÂßãÂåñÂä†ËΩΩÁïåÈù¢ (Loading screen)
  // -----------------------
  if (!isInitialized) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <div className="w-8 h-8 border-4 border-primary-500 border-t-transparent rounded-full animate-spin mx-auto mb-4"></div>
          <p className="text-gray-600">Initializing LLM Practice App...</p>
        </div>
      </div>
    );
  }

  // -----------------------
  // ‰∏ªÂ∏ÉÂ±ÄÊ∏≤Êüì
  // -----------------------
  return (
    <div className="min-h-screen bg-gray-50 flex flex-col">
      {/* È°∂ÈÉ® Header */}
      <header className="bg-white shadow-sm border-b border-gray-200">
        <div className="max-w-4xl mx-auto px-4 py-4">
          <div className="flex items-center justify-between">
            <div>
              <h1 className="text-2xl font-bold text-gray-900">LLM Practice</h1>
              <p className="text-sm text-gray-600">
                Practice chatting with Large Language Models
              </p>
            </div>

            <div className="flex items-center space-x-4">
              {/* API Áä∂ÊÄÅÊåáÁ§∫Âô® */}
              {apiStatus && (
                <div className="flex items-center space-x-2">
                  <div className={`w-3 h-3 rounded-full ${
                    apiStatus.overall === 'healthy' ? 'bg-green-500' : 
                    apiStatus.overall === 'degraded' ? 'bg-yellow-500' : 'bg-red-500'
                  }`}></div>
                  <span className="text-sm text-gray-600">
                    {apiStatus.overall === 'healthy' ? 'Connected' : 'Issues detected'}
                  </span>
                  <button
                    onClick={refreshStatus}
                    className="text-xs text-primary-600 hover:text-primary-700"
                  >
                    Refresh
                  </button>
                </div>
              )}
            </div>
          </div>

          {/* ÂØºËà™ËèúÂçï */}
          <nav className="mt-4">
            <div className="flex space-x-1">
              {navigationItems.map((item) => (
                <Link
                  key={item.path}
                  to={item.path}
                  className={`px-3 py-2 rounded-md text-sm font-medium transition-colors flex items-center space-x-2 ${
                    location.pathname === item.path
                      ? 'bg-primary-100 text-primary-700'
                      : 'text-gray-600 hover:text-gray-900 hover:bg-gray-100'
                  }`}
                >
                  <span>{item.icon}</span>
                  <span>{item.label}</span>
                </Link>
              ))}
            </div>
          </nav>
        </div>
      </header>

      {/* ÈîôËØØÊèêÁ§∫ Banner */}
      {error && (
        <div className="bg-red-50 border-l-4 border-red-400 p-4">
          <div className="max-w-4xl mx-auto">
            <div className="flex items-center">
              <div className="flex-shrink-0">
                {/* ÈîôËØØÂõæÊ†á */}
                <svg className="h-5 w-5 text-red-400" viewBox="0 0 20 20" fill="currentColor">
                  <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zM8.707 7.293a1 1 0 00-1.414 1.414L8.586 10l-1.293 1.293a1 1 0 101.414 1.414L10 11.414l1.293 1.293a1 1 0 001.414-1.414L11.414 10l1.293-1.293a1 1 0 00-1.414-1.414L10 8.586 8.707 7.293z" clipRule="evenodd" />
                </svg>
              </div>
              <div className="ml-3">
                <p className="text-sm text-red-700">{error}</p>
              </div>
              <div className="ml-auto">
                <button
                  onClick={() => setError(null)}
                  className="text-red-400 hover:text-red-600"
                >
                  <svg className="h-5 w-5" viewBox="0 0 20 20" fill="currentColor">
                    <path fillRule="evenodd" d="M4.293 4.293a1 1 0 011.414 0L10 8.586l4.293-4.293a1 1 0 111.414 1.414L11.414 10l4.293 4.293a1 1 0 01-1.414 1.414L10 11.414l-4.293 4.293a1 1 0 01-1.414-1.414L8.586 10 4.293 5.707a1 1 0 010-1.414z" clipRule="evenodd" />
                  </svg>
                </button>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* ‰∏ª‰ΩìÂÜÖÂÆπÂå∫ */}
      <main className="flex-1 overflow-hidden">
        <div className="max-w-4xl mx-auto h-full">
          {children}
        </div>
      </main>
    </div>
  );
};

export default Layout;
